        [Test]
        [TestCase("(())", 0)]
        [TestCase("()()", 0)]
        [TestCase("(((", 3)]
        [TestCase("(()(()(", 3)]
        [TestCase("))(((((", 3)]
        [TestCase("())", -1)]
        [TestCase("))(", -1)]
        [TestCase(")))", -3)]
        [TestCase(")())())", -3)]
        public void Should_end_on_correct_floor(string input, int expected)
        {
            var i = CalculateEndingFloor(input);
            i.Should().Be(expected);
        }

        private static int CalculateEndingFloor(string input)
        {
            var charArray = input.ToCharArray();
            var down = charArray.Where(x => x == ')').Count();
            var up = charArray.Where(x => x == '(').Count();
            var i = up - down;
            return i;
        }
