        [Test]
        [TestCase(")", 1)]
        [TestCase("()())", 5)]
        public void Should_be_able_to_find_when_santa_is_entering_the_basement(string input, int expected)
        {
            var numberOfSteps = FindWhenSantaIsEnteringTheBasement(input);

            numberOfSteps.Should().Be(expected);
        }

        private static int FindWhenSantaIsEnteringTheBasement(string input)
        {
            var charArray = input.ToCharArray();

            var start = 0;
            var numberOfSteps = 0;
            foreach (var step in charArray)
            {
                numberOfSteps++;
                if (step == ')')
                    start--;
                else
                    start++;

                if (start < 0)
                    break;
            }
            return numberOfSteps;
        }
